    return Array.from(visitedRoutes).some((path) => path.startsWith("/admin"));
  }, [visitedRoutes]);

  // If visited, mount immediately. Else wait for 100ms.
  useEffect(() => {
    if (hasVisitedAdmin) {
      setIsMounted(true);
    } else {
      const timer = setTimeout(() => {
        setIsMounted(true);
      }, 100);
      return () => clearTimeout(timer);
    }
  }, [hasVisitedAdmin]);

  // Admin Data Specifics
